// let slide = pptx.addSlide();

// var pptx = new PptxGenJS();
pptx.layout = 'LAYOUT_WIDE';
/*
slide.addText(
  "React Demo!",
  { y:1, w:10, fontSize:36, fill:'F1F1F1', align:'center' }
);*/



// pptx.writeFile("react-demo5.pptx");

export default class DownPpt extends React.Component {

	constructor(props) {
		super(props);
		 this.state = {

				Description  : " BY"
		 }
	}


DownPpt(event){
	event.preventDefault();
	pptx.defineSlideMaster({
		  title : 'MASTER_SLIDE',
		  margin: [0.5, 0.25, 1.00, 0.25],
		  bkgd  : 'FFFFFF',
		  objects: [
		    {image: { x:11.45, y:5.95, w:1.67, h:0.75,  }},/*data:starlabsLogoSml*/
		    {rect:  { x:0, y:6.9, w:'100%', h:0.6, fill:'003b75' }},
		    {text:  {
		        text:'iAssure IT',
		        options:{x:0, y:6.9, w:'100%', align:'center', color:'FFFFFF',align:'center', fontSize:12}
		    }}
		  ],
		  slideNumber: { x:1.0, y:7.0, color:'FFFFFF' }
		});
	let slide = pptx.addSlide('MASTER_SLIDE');
	slide.addText(this.state.Description, { x:0.5, y:0.7, fontSize:18 ,align:'center'});

	swal({
          title: "Are you sure you want to Download as PPT  ?",
          text: "",
          icon: "success",
          buttons: true,
          dangerMode: true,
        })
        .then((success) => {
            if (success) {
            	pptx.writeFile("Sample Presentation.pptx");

            } else {

          }
        }); 
}

	render() {
		return (
			<div>
				<button type="button" class="btn btn-primary" onClick={this.DownPpt.bind(this)}>Click here to gentrate </button>
				<table className="table" id="tableId">
						  	<thead className="thead-dark">
						    	<tr className="">
							      <th scope="col">No.</th>
							      <th scope="col">Page Title</th>
							      <th scope="col">Page Url</th>
							      <th scope="col">Action</th>
						    	</tr>
						  	</thead>
						    <tbody>

											    <tr >
											      <td>"1"</td>
											      <td >pageTitle</td>
											      <td className="onHoverClick" data-placement="top" title="Click here to view Page">{result.pageURL}</td>
											      <td><i className="fa fa-edit deletIcon" data-toggle="collapse" data-toggle="modal" data-target="#myModal"data-placement="top" title="Edit Page"></i>&nbsp;&nbsp;&nbsp;&nbsp;
											      		<i className="fa fa-trash deletIcon"   data-placement="top" title="Delete page"></i>&nbsp;&nbsp;&nbsp;&nbsp;
											      		{/*<i className="fa fa-eye"></i>*/}
											      </td>
											    </tr>

						    </tbody>
						</table>
			</div>
		);
	}
}
