import React from 'react';
import "./Bdesignblock.css";

export default class Bdesignblock extends React.Component {

    constructor(props) {
    super(props);
    this.state = {
      blocks: {
        "blockTitle": "",
        "blockSubTitle": "",
        "blockDescription": "<b>YOU DESERVE THE <br/>HIGHLY CUSTOMISABLE, LUXERIOUS & HAND CRAFTED ONLINE STORE OR ECOMMERCE WEBSITE FOR YOUR PRECIOUS CLIENT ENGAGEMENT</b>",
        "blockComponentName": "",
        "blockType": "",
        "bgImage": "/images/1.png",
        "fgImage": "/images/37.png",
        "repeatedBlocks": [
        					{ 
        						Title: "Sample 1", 
        						SubTitle: "", 
        						Link: "", 
        						Image: "/images/4.png",
        						Description: ""
        					}
        ],
        "bgVideo"				: "",
        "fgVideo"				: "",
        "blockGroup"			: "",
        "blockAppearOnPage"		: ""
      },
      blockID:"",
      block_id:""
    };   
  }
componentDidMount(){
/*console.log("==>",this.props.block_id);*/
          {
             axios
                .get('http://qaiassureitapi.iassureit.com/api/blocks/get/'+this.props.block_id)
                .then((response)=>{
                    if(response.data){
                    	// console.log("ListofServices =",response.data);
                      this.setState({
                          blocks:response.data
                      });
                    }                  
                  })           
                .catch(function(error){
                  console.log(error);
              })
            }
      this.setState({
                block_id:this.props.block_id
              });
}


	render() {
		return (
			<div className="container-fluid nopadding ecBox4">
				<img className="img-responsive pull-right" src="/images/ec11.png" alt="Bannerpng" style={{marginTop:"-1px",zIndex:"-1"}}/>
