import React from 'react';
import pptxgen from "pptxgenjs";
import swal from 'sweetalert';


let pptx = new pptxgen();
// let slide = pptx.addSlide();

// var pptx = new PptxGenJS();
pptx.layout = 'LAYOUT_WIDE';
/*
slide.addText(
  "React Demo!",
