// pptx.writeFile("react-demo5.pptx");

export default class DownPpt extends React.Component {

	constructor(props) {
		super(props);
		 this.state = {

				Description  : " BY"
		 }
	}


DownPpt(event){
	event.preventDefault();
	var printContents = document.getElementById('paraId').innerHTML;
	var printTableContents = document.getElementById('tableId').innerHTML;


	pptx.defineSlideMaster({
		  title : 'MASTER_SLIDE',
		  margin: [0.5, 0.25, 1.00, 0.25],
		  bkgd  : 'FFFFFF',
		  objects: [
		    {image: { x:0, y:0, w:10, h:2, path:'/images/header.png'}},/*data:starlabsLogoSml*/

		    {text:  {
		        text:'iAssure IT',
		        options:{x:0, y:6.9, w:'100%', align:'center',fontSize:24, color:'FFFFFF',align:'center', fontSize:12}
		    }}
		  ],
		  slideNumber: { x:1.0, y:7.0, color:'FFFFFF' }
		});
		let slide = pptx.addSlide('MASTER_SLIDE');
		let slide1 = pptx.addSlide('MASTER_SLIDE');
		let slide2 = pptx.addSlide('MASTER_SLIDE');


			slide.addText(printContents, { x:0.5, y:0.7, fontSize:18 ,align:'center'});
			// slide.addText(printContents, { x:0.5, y:0.7, fontSize:18 ,align:'center'});
			// pptx.tableToSlides("tableId", { master: "MASTER_SLIDE" });

			/*slide1.addText(
