import React 		from 'react';
import CKEditor 	from 'ckeditor4-react';
import axios 		from 'axios';
import $ 			from 'jquery';
import swal 		from 'sweetalert';

import TimePicker   from 'rc-time-picker';

const format = "h:mm a";
var answersarray =[];
export default class Jobform extends React.Component {
	constructor(props) {
		super(props);
		 this.state = {
		 		jobTitle 	    : "",
				jobDescription  : "",
				CityofWork 	    : "",

				validfrom 		: "",
				arrayValues 	: [],
				skills 			: "",
				validTill 		: "",
				ListOfjobs 		: "",



		 }
	}
/*	handleChange(){

	}*/
	componentDidMount(){
		this.getListOfJobs();


	}

	handleChange(event){
		event.preventDefault();
    	this.setState({

        [event.target.name]:event.target.value


    	});
	}
	Submit(){
		var formValues = {
    		jobTitle 			: this.state.jobTitle,
    		jobDescription 		: this.state.jobDescription,
    		city 				: this.state.CityofWork,
    		validfrom 			: this.state.validfrom,
    		validTill 			: this.state.validTill,		
    		skills 				: this.state.arrayValues,		
		};
		console.log("formValues ==>",formValues);
		axios
				.post('/api/jobform/post',formValues)
			  	.then((response)=>{
			    // handle success
			   		console.log("response",response.data);
			   		window.location.reload();

			  	})
			  	.catch(function (error) {
			    // handle error
			    	console.log(error);
			  	});


	}
	getListOfJobs(){
	/*/get/list*/
	axios
			.get('/api/jobform/get/list')
			.then((response)=>{       
				console.log("response  ==",response); 
			      	this.setState({
		      			ListOfjobs:response.data
		      		});
				})
		  	.catch(function (error) {
		    // handle error
		    	console.log(error);
		  	});
	}

	deleteJob(event){
		event.preventDefault();
		var URL= event.target.id;
		console.log("id delet", URL);
		 swal({
	          title: "Are you sure you want to delete this Job ?",
	          text: "Once deleted, you will not be able to recover this Job!",
	          icon: "warning",
	          buttons: true,
	          dangerMode: true,
	        })
	        .then((success) => {
	            if (success) {
	            	axios
				    .delete("/api/jobform/delete/"+URL)
				    .then((response)=>{
				     	// this.getListOfPages();
				       swal("Your Job is deleted!");
				       window.location.reload();
				    })
				    .catch((error)=>{
				       console.log("error = ", error);              
				    }); 
	            } else {
	            swal("Your Job is safe!");
	          }
	        }); 
	}	
	editPage(event){
