import React from 'react';
import pptxgen from "pptxgenjs";


let pptx = new pptxgen();
let slide = pptx.addSlide();

slide.addText(
  "React Demo!",
  { y:1, w:10, fontSize:36, fill:'F1F1F1', align:'center' }
);
// pptx.writeFile("react-demo5.pptx");

export default class DownPpt extends React.Component {

	constructor(props) {
		super(props);
	}


DownPpt(event){
	event.preventDefault();

	pptx.writeFile("Sample Presentation.pptx");
}

	render() {
		return (
			<div>
				<button type="button" class="btn btn-primary" onClick={this.DownPpt.bind(this)}>Click here to gentrate </button>
			</div>
		);
	}
}
