import React from 'react';
import pptxgen from "pptxgenjs";


let pptx = new pptxgen();
// let slide = pptx.addSlide();

// var pptx = new PptxGenJS();
pptx.layout = 'LAYOUT_WIDE';
/*
slide.addText(
  "React Demo!",
  { y:1, w:10, fontSize:36, fill:'F1F1F1', align:'center' }
);*/
pptx.defineSlideMaster({
  title : 'MASTER_SLIDE',
  margin: [0.5, 0.25, 1.00, 0.25],
  bkgd  : 'FFFFFF',
  objects: [
    {image: { x:11.45, y:5.95, w:1.67, h:0.75,  }},/*data:starlabsLogoSml*/
    {rect:  { x:0, y:6.9, w:'100%', h:0.6, fill:'003b75' }},
    {text:  {
        text:'iAssure IT',
        options:{x:0, y:6.9, w:'100%', align:'center', color:'FFFFFF',align:'center', fontSize:12}
    }}
  ],
  slideNumber: { x:1.0, y:7.0, color:'FFFFFF' }
});


// pptx.writeFile("react-demo5.pptx");

export default class DownPpt extends React.Component {

	constructor(props) {
		super(props);
		 this.state = {

				Description  : " BY"
		 }
	}


DownPpt(event){
	event.preventDefault();
	let slide = pptx.addSlide('MASTER_SLIDE');
	slide.addText('{'+this.state.Description+'}', { x:0.5, y:0.7, fontSize:18 ,align:'center'});

	pptx.writeFile("Sample Presentation.pptx");
	this.setState({
		      			Description:"hi"
		      		});
}

	render() {
		return (
			<div>
				<button type="button" class="btn btn-primary" onClick={this.DownPpt.bind(this)}>Click here to gentrate </button>
			</div>
		);
	}
}
